/**
 * NPM Import
 */
import React, { Component } from 'react';

/**
 * LOCAL Import
 */


/**
 * CODE Here
 */

class Arial extends Component {
  state = {
    myText: 'hello',
  }
  handleChange = (event) => {
    let writeText = event.target.value;
    this.setState({myText: writeText})
  }

  render() {
    const { currentFontComponent } = this.props
    return (
      <>
        <h1>{currentFontComponent}</h1>
        <div id="container">
            <div id="left-side">
                <h2>Write your text</h2>
                <textarea 
                  onChange={this.handleChange}
                />
            </div>
            <div id="right-side">
                <h2>See the result</h2>
                <div id="result">
                  {/* Custom p style here directly with font prop (and bold if you want) */}
                  <p style={{fontFamily: `${currentFontComponent}`}}>{this.state.writeText}</p>
                </div>
            </div>
        </div>
      </>
    )
  }
}

 /**
* Export
*/
export default Arial;
