/**
 * NPM Import
 */
import React, { Component } from 'react';

/**
 * LOCAL Import
 */


/**
 * CODE Here
 */
function handleChange(event) {
  let writeText = event.target.value;
  console.log(writeText);
  return writeText;
}

class Arial extends Component {
  render() {
    const { currentFontComponent } = this.props
    return (
      <>
        <h1>{currentFontComponent}</h1>
        <div id="container">
            <div id="left-side">
                <h2>Write your text</h2>
                <textarea 
                  onChange={handleChange}
                />
            </div>
            <div id="right-side">
                <h2>See the result</h2>
                <div id="result">
                  {/* Custom p style here directly with font prop (and bold if you want) */}
                  <p style={{fontFamily: `${currentFontComponent}`}}>{writeText}</p>
                </div>
            </div>
        </div>
      </>
    )
  }
}

 /**
* Export
*/
export default Arial;
